import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import Button from "react-bootstrap/Button";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import Modal from "react-bootstrap/Modal";
import {
  Map,
  ZoomControl,
  LayersControl,
  LayerGroup,
  TileLayer,
  FeatureGroup,
  Tooltip,
  Polygon
} from "react-leaflet";
import { EditControl } from "react-leaflet-draw";
import Control from "react-leaflet-control";
import { CubeGrid } from "styled-spinkit";
import { slide as Menu } from "react-burger-menu";
import BootstrapTable from "react-bootstrap-table-next";
import cellEditFactory from "react-bootstrap-table2-editor";
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";
import "leaflet-draw/dist/leaflet.draw.css";
import styled from "styled-components";

const { Overlay } = LayersControl;

const Styles = styled.div`
  .btn-light {
    background-color: white;
    border-color: black;
    font-size: 12px;
  }

  .dropdown-item {
    color: black;
    font-size: 12px;
  }

  .form-check {
    font-size: 12px;
  }

  .leaflet-container {
    width: 100%;
    height: 91.35vh;
  }

  // NOTE: helper classes below are from react-burger-menu library
  /* Position and sizing of clickable cross button */
  .bm-cross-button {
    height: 24px;
    width: 24px;
  }

  /* Color/shape of close button cross */
  .bm-cross {
    background: black;
  }

  /* Sidebar wrapper styles  */
  .bm-menu-wrap {
    position: fixed;
    height: 100%;
  }

  /* General sidebar styles */
  .bm-menu {
    background: #cfd8dc;
    padding: 2.5em 1.5em 0;
    font-size: 1.15em;
  }

  /* Wrapper for item list */
  .bm-item-list {
    color: black;
    padding: 0;
  }

  /* Individual item */
  .bm-item {
    display: inline-block;
  }

  /* Styling of overlay */
  .bm-overlay {
    background: rgba(0, 0, 0, 0.3);
  }
`;

class MapView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      latitude: 34,
      longitude: -85,
      zoom: 6,
      displayMode: 1,
      editMode: 1,
      isLoading: false,
      sidebarOpen: false,
      currPrecinct: {
        id: null,
        precinctsIndex: null,
        canonicalName: null,
        ghost: null,
        multipleBorder: null,
        adjPrecIds: [],
        enclPrecIds: [],
        demographicData: [{ population: null }],
        electionData: [
          {
            election: "2016 Presidential",
            demVotes: null,
            repVotes: null
          },
          {
            election: "2016 Congressional",
            demVotes: null,
            repVotes: null
          },
          {
            election: "2018 Congressional",
            demVotes: null,
            repVotes: null
          }
        ],
        logBag: [],
        county: {
          id: null,
          canonicalName: null,
          ethnicityData: [
            {
              ethnicity: "White",
              population: null
            },
            {
              ethnicity: "Black or African American",
              population: null
            },
            {
              ethnicity: "Asian or Asian American",
              population: null
            },
            {
              ethnicity: "American Indian",
              population: null
            },
            {
              ethnicity: "Pacific Islander",
              population: null
            },
            {
              ethnicity: "Others",
              population: null
            }
          ],
          state: {
            id: null,
            canonicalName: null
          }
        },
        coordinates: []
        // TODO: Add more properties
      },
      states: [],
      counties: [],
      precincts: [],
      // NOTE: The two features below are not supported yet
      nationalParks: [],
      congDistricts: [],
      // NOTE: The feature below is for testing boundary correction
      testPolygons: [
        {
          id: 1,
          fillColor: "#fff9c4",
          coordinates: [
            [
              [37, -85],
              [37.25, -84.75],
              [38, -85],
              [38, -84],
              [37, -84]
            ]
          ]
        },
        {
          id: 2,
          fillColor: "#fff9c4",
          coordinates: [
            [
              [37, -84],
              [38, -84],
              [38.5, -83.5],
              [38, -83],
              [37, -83]
            ]
          ]
        }
      ]
    };
  }

  handleStateChange(state) {
    this.setState({ sidebarOpen: state.isOpen });
  }

  handleMouseOver(e) {
    e.target.openTooltip();
  }

  handleMouseOut(e) {
    e.target.closeTooltip();
  }

  handleZoomEnd(e) {
    if (e.target._zoom < 7 && this.state.displayMode === 2) {
      this.setState({ zoom: e.target._zoom, displayMode: 1, counties: [] });
    } else if (e.target._zoom < 10 && this.state.displayMode === 3) {
      this.setState({ zoom: e.target._zoom, displayMode: 2, precincts: [] });
    } else {
      this.setState({ zoom: e.target._zoom });
    }
  }

  handleViewNeighbors(e, id) {
    if (this.state.editMode === 1) {
      // Fetch a data of a selected precinct and then highlight its neighbors
      const precinctsCopy = [...this.state.precincts];
      const precinctsIndex = precinctsCopy.findIndex((el) => el.id === id);
      precinctsCopy[precinctsIndex] = {
        ...precinctsCopy[precinctsIndex],
        fillColor: "#c8b900"
      };
      this.setState({ isLoading: true });
      fetch("api/precinct/" + id)
        .then((res) => res.json())
        .then((data) => {
          console.log(data); // DEBUG: Remove this line later
          data.adjPrecIds.forEach((adjPrecId) => {
            precinctsCopy.forEach((precinct) => {
              if (precinct.id === adjPrecId) {
                precinct.fillColor = "#ffeb3b";
              }
            });
          });
          this.setState({
            precincts: precinctsCopy
          });
          this.setState({ isLoading: false });
        });
    }
  }

  handlePrecinctClick(e, id) {
    // Modify map view and change the fill color of a selected state
    const precinctsCopy = [...this.state.precincts];
    if (this.state.currPrecinct.precinctsIndex !== null) {
      precinctsCopy[this.state.currPrecinct.precinctsIndex] = {
        ...precinctsCopy[this.state.currPrecinct.precinctsIndex],
        fillColor: "#fff9c4"
      };
    }
    const newPrecinctsIndex = precinctsCopy.findIndex((el) => el.id === id);
    precinctsCopy[newPrecinctsIndex] = {
      ...precinctsCopy[newPrecinctsIndex],
      fillColor: "#c8b900"
    };
    this.setState({
      latitude: e.latlng.lat,
      longitude: e.latlng.lng,
      zoom: 12,
      currPrecinct: {
        ...this.state.currPrecinct,
        precinctsIndex: newPrecinctsIndex
      },
      precincts: precinctsCopy
    });

    // Fetch a detailed data about a selected precinct
    const demographicDataCopy = [...this.state.currPrecinct.demographicData];
    const electionDataCopy = [...this.state.currPrecinct.electionData];
    const ethnicityDataCopy = [...this.state.currPrecinct.county.ethnicityData];
    this.setState({ isLoading: true });
    fetch("api/precinct/" + id)
      .then((res) => res.json())
      .then((data) => {
        console.log(data); // DEBUG: Remove this line later
        // demographicDataCopy[0] = { population: data.population };
        // electionDataCopy[0] = {
        //   ...electionDataCopy[0],
        //   demVotes: data.electionData.PRESIDENTIAL_16_DEM,
        //   repVotes: data.electionData.PRESIDENTIAL_16_REP
        // };
        // electionDataCopy[1] = {
        //   ...electionDataCopy[1],
        //   demVotes: data.electionData.CONGRESSIONAL_16_DEM,
        //   repVotes: data.electionData.CONGRESSIONAL_16_REP
        // };
        // electionDataCopy[2] = {
        //   ...electionDataCopy[2],
        //   demVotes: data.electionData.CONGRESSIONAL_18_DEM,
        //   repVotes: data.electionData.CONGRESSIONAL_18_REP
        // };
        ethnicityDataCopy[0] = {
          ...ethnicityDataCopy[0],
          population: data.white
        };
        ethnicityDataCopy[1] = {
          ...ethnicityDataCopy[1],
          population: data.africanAmer
        };
        ethnicityDataCopy[2] = {
          ...ethnicityDataCopy[2],
          population: data.asian
        };
        ethnicityDataCopy[3] = {
          ...ethnicityDataCopy[3],
          population: data.nativeAmer
        };
        ethnicityDataCopy[4] = {
          ...ethnicityDataCopy[4],
          population: data.pasifika
        };
        ethnicityDataCopy[5] = {
          ...ethnicityDataCopy[5],
          population: data.others
        };
        this.setState({
          currPrecinct: {
            ...this.state.currPrecinct,
            id: data.id,
            canonicalName: data.canonicalName,
            ghost: data.ghost,
            multipleBorder: data.multipleBorder,
            // demographicData: demographicDataCopy,
            // electionData: electionDataCopy,
            // TODO: add logBag
            // logBag: [
            //   ...this.state.currPrecinct.logBag,
            //   {
            //     id: null,
            //     category: null,
            //     comment: null
            //   }
            // ],
            county: {
              ethnicityData: ethnicityDataCopy
            }
            // TODO: Add more properties
          }
        });
        this.setState({ isLoading: false, sidebarOpen: true });
      });
  }

  handleCountyClick(e, id) {
    this.setState({
      latitude: e.latlng.lat,
      longitude: e.latlng.lng,
      zoom: 10,
      isLoading: true
    });
    fetch("api/county/" + id)
      .then((res) => res.json())
      .then((data) => {
        console.log(data); // DEBUG: Remove this line later
        data.precincts.map((currData) =>
          this.setState({
            precincts: [
              ...this.state.precincts,
              {
                id: currData.id,
                fillColor: "#fff9c4",
                coordinates: JSON.parse(currData.coordinates)
              }
            ]
          })
        );
        this.setState({ displayMode: 3, isLoading: false });
      });
  }

  handleStateClick(e, id) {
    // Get counties of a selected state
    this.setState({
      latitude: e.latlng.lat,
      longitude: e.latlng.lng,
      zoom: 7,
      isLoading: true
    });
    fetch("api/state/" + id)
      .then((res) => res.json())
      .then((data) => {
        console.log(data); // DEBUG: Remove this line later
        data.counties.map((currData) =>
          this.setState({
            counties: [
              ...this.state.counties,
              {
                id: currData.id,
                fillColor: "#fff9c4",
                coordinates: JSON.parse(currData.coordinates)
              }
            ]
          })
        );
        this.setState({ displayMode: 2, isLoading: false });
      });
  }

  handleStateSelect(id) {
    // Selected Kentucky
    if (id === 21) {
      this.setState({ latitude: 37.84, longitude: -84.27, zoom: 7 });
    }
    // Selected Louisiana
    else if (id === 22) {
      this.setState({ latitude: 30.98, longitude: -91.96, zoom: 7 });
    }
    // Selected South Carolina
    else if (id === 45) {
      this.setState({ latitude: 33.84, longitude: -81.16, zoom: 7 });
    }
    // Get counties of a selected state
    this.setState({ isLoading: true });
    fetch("api/state/" + id)
      .then((res) => res.json())
      .then((data) => {
        console.log(data); // DEBUG: Remove this line later
        data.counties.map((currData) =>
          this.setState({
            counties: [
              ...this.state.counties,
              {
                id: currData.id,
                fillColor: "#fff9c4",
                coordinates: JSON.parse(currData.coordinates)
              }
            ]
          })
        );
        this.setState({ displayMode: 2, isLoading: false });
      });
  }

  handleTableChange(oldValue, newValue, row) {
    // DEBUG: Remove these lines below later
    console.log(oldValue);
    console.log(newValue);
    console.log(row);

    // TODO: Make a POST request to the server with "api/precinct"
    // fetch("api/precinct", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json"
    //   },
    //   body: JSON.stringify()
    // })
    //   .then((response) => response.json())
    //   .then((data) => {
    //     console.log("Success:", data);
    //   })
    //   .catch((error) => {
    //     console.error("Error:", error);
    //   });
  }

  handlePolygonCreated(e) {
