  validateNumber(data) {
    if (isNaN(data)) {
      return {
        valid: false,
        message: "Input should be numeric"
      };
    } else if (data < 0) {
      return {
        valid: false,
        message: "Input cannot be less than zero"
      };
    } else if (data > 0 && data !== data.replace(/^0+/, "")) {
      return {
        valid: false,
        message: "Input cannot have a leading zero"
      };
    } else {
      return true;
    }
  }

